import React, { Component } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, TextInput, Linking, Platform, StyleSheet } from "react-native";
import firebase from '../database/firebase';
import { Button } from 'react-native-paper';



export default class Home extends React.Component {
  makeCall = () => {

    let phoneNumber = '';

    if (Platform.OS === 'android') {
      phoneNumber = 'tel:${+2348093298137}';
    } else {
      phoneNumber = 'telprompt:${+2348093298137}';
    }

    Linking.openURL(phoneNumber);
  };

  constructor() {
    super();
    this.state = { 
      uid: ''
    }
  }

  signOut = () => {
    firebase.auth().signOut().then(() => {
      this.props.navigation.navigate('Login')
    })
    .catch(error => this.setState({ errorMessage: error.message }))
  }  




  render() {
    this.state = { 
      displayName: firebase.auth().currentUser.displayName,
      uid: firebase.auth().currentUser.uid
    }
    return (
      <View style={{backgroundColor:"#FFF"}}>
        <ImageBackground
        source={require("./images/shop.png")}
        style={{ width: "100%", height: 130, resizeMode:"cover" }}
      >
        <Image
                source={require("./images/log.png")}
                style={{ height: 60, marginTop:58, marginHorizontal:40, width: 165 }}
              />
        <View
          style={{
            flexDirection: "row",
            marginTop: 55,
            alignItems: "center",
            paddingHorizontal: 20,
            justifyContent:"flex-end"
          }}
        >
          <Icon name="bell" size={30} color="#D4AA00" style={{ width: 50,paddingVertical:9, paddingHorizontal:9, borderRadius:25,  backgroundColor:'gray' }} />

        </View>

          </ImageBackground>

        <View style={{ marginTop:10}}>
          <View style={{paddingHorizontal: 10}}>
          <Text 
            style={{
              fontSize: 12,
              paddingHorizontal:6,
              paddingRight: 80,
              margin:6,
              lineHeight: 22,
              fontFamily: "Roboto",
              color: "#000080",
              borderRadius:20,


            }}
          >
            <Icon name="map-marker" size={12} color="red" style={{ width: 10 }} />Store: Elifimo Canal West Osapa, Lekki
          </Text>
          <View style={styles.hedContainer}>

          <Image
                source={require("./images/boy.png")}
                style={{ height: 40, marginHorizontal:10, width: 40 }}
              />
          <Text
            style={{
              fontSize: 15,
              color: "#000",
              fontFamily: "Roboto",
              flexDirection:"row",
              alignContent:"center",
              justifyContent:"center",
              marginTop:8,
              fontWeight:'bold'
            }}
          >
            Hello, {this.state.displayName}
          </Text>
          </View>
          </View>
          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginRight: -40,  backgroundColor:"#FFF", paddingHorizontal: 1, marginTop:-30}}
          >
            <View style={styles.chatContainer}>
            <TouchableOpacity
              onPress={ ()=>{ Linking.openURL('https://chat.whatsapp.com/EO9wLKN4lWm2kPf9OpJiFh')}}
              style={{
                alignItems: "flex-start",
                justifyContent: "flex-start",
                height: 90,
                width: 220,
                borderRadius: 15,
                backgroundColor:'#F0F8FF',
                paddingHorizontal:9,
                flexDirection:'row',
                paddingVertical:14,
                marginRight:7
              }}
            >
              <Image
                source={require("./images/whatsapp.png")}
                style={{ height: 60, width: 60, flexDirection:'row' }}
              />
              <View
              style={{
                alignItems: "flex-start",
                justifyContent: "center",              

              }}
            >
              <Text style={{fontWeight:'bold', marginTop:12, paddingHorizontal:9}}>Chat</Text>
              <Text style={{paddingHorizontal:9}}>On-Call Pharmacist</Text>
            </View>
            </TouchableOpacity>





            <TouchableOpacity
              onPress={this.makeCall}
              style={{
                alignItems: "flex-start",
                justifyContent: "flex-start",
                height: 90,
                width: 100,
                borderRadius: 15,
                backgroundColor:'#F0F8FF',
                paddingHorizontal:14,
                flexDirection:'row',
                paddingVertical:14,
              }}
            >
              <Image
                source={require("./images/phone-call.png")}
                style={{ height: 60, width: 60, alignContent:'center', justifyContent:'center' }}
              />
            </TouchableOpacity>
            </View>
          </ScrollView>
              <View style={{paddingHorizontal: 10}}>
          <View
            style={{
              flexDirection: "row",
              backgroundColor: "#FFF",
              borderRadius: 40,
              alignItems: "center",
              paddingVertical: 3,
              paddingHorizontal: 20,
              marginTop: 10,
              borderColor:'grey',
              borderWidth:1

            }}
          >
            <Icon name="search-web" size={15} color="blue" style={{ width: 60 }} />
            <TextInput
              placeholder="Search for Your Medication"
              style={{ paddingHorizontal: 3, paddingVertical:10, fontSize: 15, color: "#ccccef" }}
            />
          </View>
          </View>
          <View style={{flexDirection:'row'}}>
          <Text
            style={{
              color: "#6574CF",
              fontFamily: "Roboto",
              marginTop: 20,
              fontSize: 12,
              fontWeight:"bold",
              paddingHorizontal: 10,
              textAlign:'left'
            }}
          >
            Featured Products
          </Text>
          <Text
            style={{
              color: "#6574CF",
              fontFamily: "Roboto",
              marginTop: 20,
              fontSize: 12,
              fontWeight:"bold",
              alignContent:'flex-end',
              justifyContent:'flex-end'
            }}
          >
            Featured Products
          </Text>
          </View>

          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginHorizontal: 7, marginTop: 10 }}
          >
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 110,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/antibiotics.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}

