import React, { Component } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { ImageBackground, StyleSheet, Text, View, Button, Image, TouchableOpacity, TextInput } from "react-native";
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';

const image = { uri: "https://image.freepik.com/free-vector/shiny-white-gray-background-with-wavy-lines_1017-25101.jpg" };

function SplashScreen({ navigation }) {
  return (
    <View style={styles.container}>

      <ImageBackground source={image} style={styles.image}>
      <View style={styles.header}>
        <View style={styles.logoContainer}>
          <Image
            source={{
              uri: 'http://drugshoppa.ng/wp-content/uploads/2020/08/cropped-DrugShoppa-1.png',

            }}
            style={styles.logo}
          />
      </View>
      </View>
      <View style={styles.footer}>
        <TouchableOpacity onPress={() => navigation.navigate('Login')}>
          <LinearGradient
        // Button Linear Gradient
        colors={['#4c669f', '#3b5998', '#192f6a']}
        style={{ padding: 15, alignItems: 'center', borderRadius: 5 }}>
        <Text
          style={{
            backgroundColor: 'transparent',
            fontSize: 20,
            color: '#fff',
          }}>
          Get Started
        </Text>
      </LinearGradient>
        </TouchableOpacity>       
      </View>
    </ImageBackground>
    </View>
  );
}

function Login({ navigation }) {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <View>
              <View style={styles.infoContainer}>

                <TextInput style={styles.input}
                  placeholder="Enter Email "
                  placeholderTextColor="black"
                  keyboardType="email-address"
                  returnKeyType="next"
                  autoCorrect={false}
                />

                <TextInput style={styles.input}
                  placeholder="Enter Password "
                  placeholderTextColor="black"
                  returnKeyType="go"
                  secureTextEntry
                  autoCorrect={false}
                />

                <TouchableOpacity style={styles.buttonContainer}>
                    <Text style={styles.buttonText}>SIGN IN</Text>
                </TouchableOpacity>
              </View>
    </View>
    <Text style={styles.TextBtn}> Dont Have an Account</Text>
      <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
        <Text style={styles.TextBtn}>
          Sign Up
        </Text>
        </TouchableOpacity>
        <View >
          <Image
            source={{
              uri: 'http://drugshoppa.ng/wp-content/uploads/2020/08/cropped-DrugShoppa-1.png',

            }}
            style={styles.logo}
          />
      </View>
    </ImageBackground>
    </View>
  );
}

function SignUp({ navigation }) {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <View style={styles.container}>
          <StatusBar backgroundColor='#009387' barStyle="light-content"/>
        <View style={styles.header}>
            <Text style={styles.text_header}>Register Now!</Text>
        </View>
        <Animatable.View 
            animation="fadeInUpBig"
            style={styles.footer}
        >
            <ScrollView>
            <Text style={styles.text_footer}>Username</Text>
            <View style={styles.action}>
                <FontAwesome 
                    name="user-o"
                    color="#05375a"
                    size={20}
                />
                <TextInput 
                    placeholder="Your Username"
                    style={styles.textInput}
                    autoCapitalize="none"
                    onChangeText={(val) => textInputChange(val)}
                />
                {data.check_textInputChange ? 
                <Animatable.View
                    animation="bounceIn"
                >
                    <Feather 
                        name="check-circle"
                        color="green"
                        size={20}
                    />
                </Animatable.View>
                : null}
            </View>

            <Text style={[styles.text_footer, {
                marginTop: 35
            }]}>Password</Text>
            <View style={styles.action}>
                <Feather 
                    name="lock"
                    color="#05375a"
                    size={20}
                />
                <TextInput 
                    placeholder="Your Password"
                    secureTextEntry={data.secureTextEntry ? true : false}
                    style={styles.textInput}
                    autoCapitalize="none"
                    onChangeText={(val) => handlePasswordChange(val)}
                />
                <TouchableOpacity
                    onPress={updateSecureTextEntry}
                >
                    {data.secureTextEntry ? 
                    <Feather 
                        name="eye-off"
                        color="grey"
                        size={20}
                    />
                    :
                    <Feather 
