import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { ImageBackground, StyleSheet, Text, View, Button, Image, TouchableOpacity, TextInput,SafeAreaView, keyboard, KeyboardAvoidingView, AsyncStorage} from "react-native";
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';


class App extends React.Component {
  constructor(props) {
  super(props);

  this.state = {
    UserEmail: '',
    UserPassword: '',
  }
}

UserLoginFunction = () =>{

 const { UserEmail }  = this.state ;
 const { UserPassword }  = this.state ;


fetch('https://reactnativecode.000webhostapp.com/User_Login.php', {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({

    email: UserEmail,

    password: UserPassword

  })

}).then((response) => response.json())
      .then((responseJson) => {

        // If server response message same as Data Matched
       if(responseJson === 'Data Matched')
        {

            //Then open Profile activity and send user email to profile activity.
            this.props.navigation.navigate('Second', { Email: UserEmail });

        }
        else{

          Alert.alert(responseJson);
        }

      }).catch((error) => {
        console.error(error);
      });

  }

render() {
const Stack = createStackNavigator();

      return (
        <NavigationContainer>
      <Stack.Navigator initialRouteName="SplashScreen">
        <Stack.Screen name="SplashScreen" component={SplashScreen} options={{ headerShown: false }}/>
        <Stack.Screen name="Login" component={Login} options={{ headerShown: false }} />
        <Stack.Screen name="SignUp" component={SignUp} options={{ headerShown: false }}/>
        <Stack.Screen name="Dashborad" component={Dashborad} options={{ headerShown: false }}/>
      </Stack.Navigator>
    </NavigationContainer>
        )
    }
}


const image = { uri: "https://image.freepik.com/free-vector/colorful-abstract-background_23-2148471419.jpg" };



function SplashScreen({ navigation }) {
  return (
    <View style={styles.container}>

      <ImageBackground source={image} style={styles.image}>
      <View style={styles.header}>
        <View style={styles.logoContainer}>
          <Image
            source={{
              uri: 'http://drugshoppa.ng/wp-content/uploads/2020/08/cropped-DrugShoppa-1.png',

            }}
            style={styles.logo}
          />
      </View>
      </View>
      <View style={styles.footer}>
        <TouchableOpacity onPress={() => navigation.navigate('Login')}>
          <LinearGradient
        // Button Linear Gradient
        colors={['#4c669f', '#3b5998', '#192f6a']}
        style={{ padding: 15, alignItems: 'center', borderRadius: 5 }}>
        <Text
          style={{
            backgroundColor: 'transparent',
            fontSize: 20,
            color: '#fff',
          }}>
          Get Started
        </Text>
      </LinearGradient>
        </TouchableOpacity>       
      </View>
    </ImageBackground>
    </View>
  );
}


function Login({ navigation }) {
  return (
    <SafeAreaView style={styles.container}>
     <StatusBar barStyle="light-content"/>
     <KeyboardAvoidingView behabior='padding' style={styles.container}>
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <TouchableOpacity onPress={() => navigation.navigate('SplashScreen')}>
                    <Text style={styles.textnav}>
                    <AntDesign name="arrowleft" size={24} color="white" /> 
                    </Text>
                </TouchableOpacity>
      <View style={styles.container}>
      <Text style={styles.textnav}>Login</Text>
              <View style={styles.infoContainer}>

                <TextInput style={styles.input}
                  placeholder="Enter Email "
                  placeholderTextColor="white"
                  onChangeText={UserEmail => this.setState({UserEmail})}
                  keyboardType="email-address"
                  returnKeyType="next"
                  autoCorrect={false}
                />

                <TextInput style={styles.input}
                  placeholder="Enter Password "
                  onChangeText={UserPassword => this.setState({UserPassword})}
                  placeholderTextColor="white"
                  returnKeyType="go"
                  secureTextEntry
                  autoCorrect={false}
                />

                <TouchableOpacity style={styles.buttonContainer} onPress={this.UserLoginFunction}>
                    <Text style={styles.buttonText}>SIGN IN</Text>
                </TouchableOpacity>
              </View>
    </View>
      <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
        <Text style={styles.TextBtn}>
        Dont Have an Account
          Sign Up
        </Text>
        </TouchableOpacity>

    </ImageBackground>
    </View>
    </KeyboardAvoidingView>
    </SafeAreaView>
  );
}

function SignUp({ navigation }) {
  return (
    <SafeAreaView style={styles.container}>
     <StatusBar barStyle="light-content"/>
     <KeyboardAvoidingView behabior='padding' style={styles.container}>
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <TouchableOpacity onPress={() => navigation.navigate('Login')}>
                    <Text style={styles.textnav}>
                    <AntDesign name="arrowleft" size={24} color="white" /> 
                    </Text>
                </TouchableOpacity>
      <View style={styles.container}>
      <Text style={styles.textnav}>Sign Up</Text>

              <View style={styles.infoContainer}>


                <TextInput style={styles.input}
                  placeholder="Full Name "
                  placeholderTextColor="white"
                  returnKeyType="next"
                  autoCorrect={false}
                />
                <TextInput style={styles.input}
                  placeholder="Enter Email "
                  placeholderTextColor="white"
                  keyboardType="email-address"
                  returnKeyType="next"
                  autoCorrect={false}
                />
                <TextInput style={styles.input}
                  placeholder="Phone Number "
                  placeholderTextColor="white"
                  returnKeyType="next"
                  autoCorrect={false}
                />

                <TextInput style={styles.input}
                  placeholder="Enter Password "
                  placeholderTextColor="white"
                  returnKeyType="go"
                  secureTextEntry
                  autoCorrect={false}
                />


                <TouchableOpacity style={styles.buttonContainer}>
                    <Text style={styles.buttonText}>SIGN UP</Text>
                </TouchableOpacity>
              </View>
            </View>
            <TouchableOpacity onPress={() => navigation.navigate('Login')}>
        <Text style={styles.TextBtn}>
        Already Have an Account
          Login
        </Text>
        </TouchableOpacity>
    </ImageBackground>
    </View>
    </KeyboardAvoidingView>
    </SafeAreaView>
  );
}


export default App;


const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
  },
  image: {
    flex: 1,
    resizeMode: "cover",
