import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, TextInput, Linking, Platform, StyleSheet,SafeAreaView } from "react-native";
import firebase from '../database/firebase';
import { Button } from 'react-native-paper';



export default class Home extends React.Component {
  makeCall = () => {

    let phoneNumber = '';

    if (Platform.OS === 'android') {
      phoneNumber = 'tel:${+2348093298137}';
    } else {
      phoneNumber = 'telprompt:${+2348093298137}';
    }

    Linking.openURL(phoneNumber);
  };

  constructor() {
    super();
    this.state = { 
      uid: ''
    }
  }

  signOut = () => {
    firebase.auth().signOut().then(() => {
      this.props.navigation.navigate('Login')
    })
    .catch(error => this.setState({ errorMessage: error.message }))
  }  




  render() {
    this.state = { 
      displayName: firebase.auth().currentUser.displayName,
      uid: firebase.auth().currentUser.uid
    }
    return (
      <SafeAreaView style={styles.safeContainer}>
     <StatusBar barStyle="light-content"/>
      <View style={{backgroundColor:"#FFF"}}>
       <View style={{ marginTop:50}}>
          <View style={{paddingHorizontal: 10}}>
          <View style={styles.hedContainer}>

          <Icon name="cart" size={40} color="#FFF" style={{  padding:10, width: 80 }} />
          <Text
            style={{
              fontSize: 15,
              color: "#FFF",
              fontFamily: "Roboto",
              flexDirection:"row",
              alignContent:"center",
              justifyContent:"center",
              marginTop:18
            }}
          >
            SHOP, {this.state.displayName}
          </Text>
          </View>
          </View>
          <View style={{paddingHorizontal: 10}}>
          <View
            style={{
              flexDirection: "row",
              backgroundColor: "#FFF",
              borderRadius: 40,
              alignItems: "center",
              paddingVertical: 3,
              paddingHorizontal: 20,
              marginTop: 10,
              shadowColor: 'black',
                shadowOffset: {width: 0, height: 2},
                shadowOpacity: 0.3,
                elevation: 10,

