import React, { Component } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { Button,View, Text, Image, ImageBackground, TextInput, Linking, Platform, StyleSheet } from "react-native";
import firebase from '../database/firebase';
import DrawerScreen from './DrawerScreen';



export default class Home extends React.Component {
  makeCall = () => {

    let phoneNumber = '';

    if (Platform.OS === 'android') {
      phoneNumber = 'tel:${08144707400}';
    } else {
      phoneNumber = 'telprompt:${08144707400}';
    }

    Linking.openURL(phoneNumber);
  };

  constructor() {
    super();
    this.state = { 
      uid: ''
    }
  }

  signOut = () => {
    firebase.auth().signOut().then(() => {
      this.props.navigation.navigate('Login')
    })
    .catch(error => this.setState({ errorMessage: error.message }))
  }  




  render() {
    this.state = { 
      displayName: firebase.auth().currentUser.displayName,
      uid: firebase.auth().currentUser.uid
    }
    return (
      <ImageBackground
        source={require("./images/bg.jpg")}
        style={{ width: "100%", height: "100%" }}
      >

        <View
          style={{
            flexDirection: "row",
            marginTop: 40,
            alignItems: "center",
            paddingHorizontal: 3,
          }}
        >
          <Icon name="menu" size={30} color="#a2a2db" style={{ width: 20 }} />
          <TouchableOpacity
          onPress={() => this.signOut()}

            >
          </TouchableOpacity>
        </View>

        <View style={{ paddingHorizontal: 40, marginTop: 25 }}>
          <Text
            style={{
              fontSize: 20,
              color: "#522289",
              fontFamily: "RobotoBold",
            }}
          >
            Hello, {this.state.displayName}
          </Text>

          <Text
            style={{
              fontSize: 15,
              paddingVertical: 3,
              paddingRight: 80,
              lineHeight: 22,
              fontFamily: "RobotoRegular",
              color: "#a2a2db",
            }}
          >
            PHARMACEUTICAL SERVICES, AT YOUR CONVENIENCE
                ONLINE SERVICES
          </Text>

          <View
            style={{
              flexDirection: "row",
              backgroundColor: "#FFF",
              borderRadius: 40,
              alignItems: "center",
              paddingVertical: 10,
              paddingHorizontal: 20,
              marginTop: 30,
            }}
          >
            <Image
              source={require("./images/search.png")}
              style={{ height: 14, width: 14 }}
            />
            <TextInput
              placeholder="Lorem ipsum"
              style={{ paddingHorizontal: 20, fontSize: 15, color: "#ccccef" }}
            />
          </View>

          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginRight: -40, marginTop: 30 }}
          >
            <View style={{backgroundColor:"#5facdb", flexDirection:"row", padding:15, borderRadius:20}}>
            <TouchableOpacity

              style={{
                alignItems: "center",
                justifyContent: "center",
                height: 66,
                width: 66,
                borderRadius: 50,
                backgroundColor: "#ffffff",
              }}
            >
              <Image
                source={require("./images/whatsapp.png")}
                style={{ height: 24, width: 24 }}
              />
            </TouchableOpacity>

            <View
              style={{
                alignItems: "center",
                justifyContent: "center",
                height: 66,
                width: 66,
                borderRadius: 50,

                marginHorizontal: 22,
              }}
            >

            </View>

            <View
              style={{
                alignItems: "center",
                justifyContent: "center",
                height: 66,
                width: 66,
                borderRadius: 50,

              }}
            >

            </View>

            <TouchableOpacity
              onPress={this.makeCall}
              style={{
                alignItems: "center",
                justifyContent: "center",
                height: 66,
                width: 66,
                borderRadius: 50,
                backgroundColor: "#ffffff",
              }}
            >
              <Image
                source={require("./images/phone.png")}
                style={{ height: 24, width: 24 }}
              />
            </TouchableOpacity>
            </View>
          </ScrollView>

          <Text
            style={{
              color: "#FFF",
              fontFamily: "RobotoRegular",
              marginTop: 50,
              fontSize: 17,
            }}
          >
            Top Category
          </Text>

          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginHorizontal: 7, marginTop: 30 }}
          >
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
          </ScrollView>
          <View style={styles.refill}>
            <Text style={{justifyContent:"center", alignContent:"center"}} >
            <Button title={"Refil Your Medications"} 
            color="blue"
            style={{justifyContent:"center", alignContent:"center"}}
            />
            </Text>

          </View>
        </View>
      </ImageBackground>
    );
  }
}

const styles = StyleSheet.create({
refill:{
  paddingVertical:50,
  alignItems: 'center',
  justifyContent: 'center',
},
})
