import React from 'react';
import { ScrollView, StyleSheet, Text, View, FlatList, TouchableOpacity, Image } from 'react-native';
import axios from 'axios';

import WooApi from './configs';


var data = '';

var config = {
  method: 'get',
  url: 'http://drugshoppa.ng/wp-json/wc/v3/products?oauth_consumer_key=ck_aaef5c823b0fabaf2958613ac0c8f60520f42150&oauth_signature_method=HMAC-SHA1&oauth_timestamp=1604806276&oauth_nonce=AzTsmgmM34o&oauth_version=1.0&oauth_signature=U44TcPLd2vxA2iy5ZsLaCph1XWg=',
  headers: { 
    'Cookie': 'woocommerce_items_in_cart=1; woocommerce_cart_hash=ed370e9e1c0f7f7319f187d604e541b4; wp_woocommerce_session_9d38f577914e427031e8dd6d8b2c643f=1%7C%7C1604979030%7C%7C1604975430%7C%7C7aa48d633811247186181d52fc2e811d'
  },
  data : data
};

axios(config)
.then(function (response) {
  console.log(JSON.stringify(response.data));
})
.catch(function (error) {
  console.log(error);
});


export default class ProductsScreen extends React.Component {
  static navigationOptions = {
    title: 'Products',
  };

  state = {
    products: []
  }



  renderItem = ({item}) => (
    <TouchableOpacity 
      style={styles.listItem} 
      onPress={() => this.props.navigation.navigate("SingleProduct", { product: item })}
    >
      <View style={styles.view}>
        <Image style={styles.image} source={{ uri: item.images[0].src }} />
        <Text style={styles.text}>{item.name}</Text>
      </View>
    </TouchableOpacity>
  )

  render() {
    return (
      <ScrollView style={styles.container}>
        {
          this.state.products.length ?
          <FlatList
            contentContainerStyle={styles.list} 
            numColumns={2}
            data={this.state.products}
            keyExtractor={ item => item.id.toString() }
            renderItem={this.renderItem}
          />
          :
          <View style={styles.loaderContainer}>
            <Image
              source={ require('./images/cart-loading.gif') }
              style={styles.loaderImage}
            />
          </View>
        }
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
    backgroundColor: '#f6f6f6',
  },
  list: {
    flexDirection: 'column'
  },
  listItem: {
    width: '50%'
  },
  view: {
    padding: 10
  },
  image: {
    width: 150, 
    height: 150
  },
  text: {
    textAlign: 'center',
    fontSize: 16,
    padding: 5
  },
  loaderContainer: {
    alignItems: 'center', 
    justifyContent: 'center',
  },
  loaderImage: {
    width: 200,
    height: 200,
  },
});
