/* -- Bottom navigation: quick navigation in bottom bar */
import * as React from 'react';
import { BottomNavigation, Text } from 'react-native-paper';
import { StyleSheet, View, Image } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import {AntDesign} from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';
import { FontAwesome } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';

const StateRoute = () => <Text>
<View style={styles.container}>
<Image style={styles.stretch} source={require('http://drugshoppa.ng/wp-content/uploads/2020/08/slider3_50.png')} />
</View>
</Text>;

const ClientRoute = () => <Text>Profile</Text>;

const SettingRoute = () => <Text>shop</Text>;

const MembershipRoute = () => <Text>Membership</Text>

const TransactionRoute = () => <Text>Transaction</Text>


export default class BottomNavigatonComponent extends React.Component {
  state = {
    index: 0,
    routes: [
      { key: 'state', title: 'Home', icon: 'home' },
      { key: 'client', title: 'Profile', icon: 'cogs' },
      { key: 'setting', title: 'Shop', icon: 'shopping-bag' },
      { key: 'Membership', title: 'Membership', icon: 'user' },
      { key: 'Transaction', title: 'Transaction', icon: 'wallet' },
    ],
  };

  // Vi sao ham 1 tham so => khi goi khong co tham so
  // truyen index vao setState co {} ????
  _handleIndexChange = index => this.setState({ index });

  _renderScene = BottomNavigation.SceneMap({
    state: StateRoute,
    client: ClientRoute,
    setting: SettingRoute,
    Membership: MembershipRoute,
    Transaction: TransactionRoute,

  });

  render() {
    return (
      <BottomNavigation
        navigationState={this.state}
        onIndexChange={this._handleIndexChange}
        renderScene={this._renderScene}
      />
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
  },
  stretch:{
    width: 50,
    height: 200,
    resizeMode: 'stretch',
  }
});
