import React, { Component } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { ImageBackground, StyleSheet, Text, View, Button, Image } from "react-native";
import { LinearGradient } from 'expo-linear-gradient';

const image = { uri: "https://image.freepik.com/free-vector/shiny-white-gray-background-with-wavy-lines_1017-25101.jpg" };

function SplashScreen({ navigation }) {
  return (
    <View style={styles.container}>

      <ImageBackground source={image} style={styles.image}>
      <View style={styles.header}>
        <View style={styles.logoContainer}>
          <Image
            source={{
              uri: 'http://drugshoppa.ng/wp-content/uploads/2020/08/cropped-DrugShoppa-1.png',

            }}
            style={styles.logo}
          />
      </View>
      </View>
      <View style={styles.footer}>
      <LinearGradient
        // Button Linear Gradient
        colors={['#4c669f', '#3b5998', '#192f6a']}
        style={{ padding: 15, alignItems: 'center', borderRadius: 5 }}>
        <Text
          style={{
            backgroundColor: 'transparent',
            fontSize: 15,
            color: '#fff',
          }}>
        </Text>
        <Button
        title="Get Started"
        onPress={() => navigation.navigate('Login')}
      />
      </LinearGradient>

      </View>
    </ImageBackground>
    </View>
  );
}

function Login({ navigation }) {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <Text style={styles.text}>Login</Text>
      <Button
        title="SignUp"
        onPress={() => navigation.navigate('SignUp')}
      />
    </ImageBackground>
    </View>
  );
}

function SignUp({ navigation }) {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <Text style={styles.text}>SignUp</Text>
      <Button
        title="Login"
        onPress={() => navigation.navigate('Login')}
      />
    </ImageBackground>
    </View>
  );
}

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="SplashScreen">
        <Stack.Screen name="SplashScreen" component={SplashScreen} options={{ headerShown: false }}/>
        <Stack.Screen name="Login" component={Login} />
        <Stack.Screen name="SignUp" component={SignUp} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column"
  },
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  header:{
    flex: 2,
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer:{
    flex:1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  text: {
    color: "grey",
    fontSize: 30,
    fontWeight: "bold"
  },
  logoContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',

  },
  logo:{
    width: 300,
    height: 66,
  },
});
