import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { ImageBackground, StyleSheet, Text, View, Button, Image, TouchableOpacity, TextInput,SafeAreaView, keyboard, KeyboardAvoidingView } from "react-native";
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';

const image = { uri: "https://image.freepik.com/free-vector/colorful-abstract-background_23-2148471419.jpg" };

function SplashScreen({ navigation }) {
  return (
    <View style={styles.container}>

      <ImageBackground source={image} style={styles.image}>
      <View style={styles.header}>
        <View style={styles.logoContainer}>
          <Image
            source={{
              uri: 'http://drugshoppa.ng/wp-content/uploads/2020/08/cropped-DrugShoppa-1.png',

            }}
            style={styles.logo}
          />
      </View>
      </View>
      <View style={styles.footer}>
        <TouchableOpacity onPress={() => navigation.navigate('Login')}>
          <LinearGradient
        // Button Linear Gradient
        colors={['#4c669f', '#3b5998', '#192f6a']}
        style={{ padding: 15, alignItems: 'center', borderRadius: 5 }}>
        <Text
          style={{
            backgroundColor: 'transparent',
            fontSize: 20,
            color: '#fff',
          }}>
          Get Started
        </Text>
      </LinearGradient>
        </TouchableOpacity>       
      </View>
    </ImageBackground>
    </View>
  );
}

function Login({ navigation }) {
  return (
    <SafeAreaView style={styles.container}>
     <StatusBar barStyle="light-content"/>
     <KeyboardAvoidingView behabior='padding' style={styles.container}>
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <TouchableOpacity onPress={() => navigation.navigate('SplashScreen')}>
                    <Text style={styles.textnav}>
                    <AntDesign name="arrowleft" size={24} color="white" /> 
                    </Text>
                </TouchableOpacity>
      <View style={styles.container}>
      <Text style={styles.textnav}>Login</Text>
              <View style={styles.infoContainer}>

                <TextInput style={styles.input}
                  placeholder="Enter Email "
                  placeholderTextColor="white"
                  keyboardType="email-address"
                  returnKeyType="next"
                  autoCorrect={false}
                />

                <TextInput style={styles.input}
                  placeholder="Enter Password "
                  placeholderTextColor="white"
                  returnKeyType="go"
                  secureTextEntry
                  autoCorrect={false}
                />

                <TouchableOpacity style={styles.buttonContainer}>
                    <Text style={styles.buttonText}>SIGN IN</Text>
                </TouchableOpacity>
              </View>
    </View>
      <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
        <Text style={styles.TextBtn}>
        Dont Have an Account
          Sign Up
        </Text>
        </TouchableOpacity>

    </ImageBackground>
    </View>
    </KeyboardAvoidingView>
    </SafeAreaView>
  );
}

function SignUp({ navigation }) {
  return (
    <View style={styles.container}>
      <ImageBackground source={image} style={styles.image}>
      <TouchableOpacity onPress={() => navigation.navigate('Login')}>
                    <Text style={styles.textnav}>
                    <AntDesign name="arrowleft" size={24} color="white" /> 
                    </Text>
                </TouchableOpacity>
      <View style={styles.container}>
      <Text style={styles.textnav}>Sign Up</Text>
              <View style={styles.infoContainer}>
                <TextInput style={styles.input}
                  placeholder="Full Name "
                  placeholderTextColor="white"
                  returnKeyType="next"
                  autoCorrect={false}
                />
                <TextInput style={styles.input}
                  placeholder="Enter Email "
                  placeholderTextColor="white"
                  keyboardType="email-address"
                  returnKeyType="next"
                  autoCorrect={false}
                />
                <TextInput style={styles.input}
                  placeholder="Phone Number "
                  placeholderTextColor="black"
                  returnKeyType="next"
                  autoCorrect={false}
                />

                <TextInput style={styles.input}
                  placeholder="Enter Password "
                  placeholderTextColor="black"
                  returnKeyType="go"
                  secureTextEntry
                  autoCorrect={false}
                />

                <TouchableOpacity style={styles.buttonContainer}>
                    <Text style={styles.buttonText}>SIGN IN</Text>
                </TouchableOpacity>
              </View>
            </View>
            <TouchableOpacity onPress={() => navigation.navigate('Login')}>
        <Text style={styles.TextBtn}>
        Already Have an Account
          Login
        </Text>
        </TouchableOpacity>
    </ImageBackground>
    </View>
  );
}

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="SplashScreen">
        <Stack.Screen name="SplashScreen" component={SplashScreen} options={{ headerShown: false }}/>
        <Stack.Screen name="Login" component={Login} options={{ headerShown: false }} />
        <Stack.Screen name="SignUp" component={SignUp} options={{ headerShown: false }}/>
      </Stack.Navigator>
    </NavigationContainer>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
  },
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  header:{
    flex: 2,
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer:{
    flex:1,
    alignItems: 'center',
    justifyContent: 'center',
  },

  text: {
    color: "grey",
    fontSize: 30,
    fontWeight: "bold"
  },
  logoContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',

  },
  logo:{
    width: 300,
    height: 66,
  },
  infoContainer:{
    position: 'absolute',
    left: 0,
    right: 0,
    bottom: 0,
    marginTop:0,
    padding: 30,

  },
  input: {
    borderRadius: 1,
    color: 'black',
    marginBottom:10,
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderColor: 'grey'
  },
  buttonContainer:{
    backgroundColor: '#3C99DC',
    paddingVertical: 10,
    paddingHorizontal:40,
    borderRadius: 10,
  },
  buttonText:{
    textAlign: 'center',
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
  TextBtn:{
    textAlign: 'center',
    color: 'blue',
    fontSize: 18,
    paddingVertical:40,
  },
  textnav:{
    fontSize: 19,
    color: 'white',
    paddingHorizontal: 20,
    paddingVertical:50,
  }
});
