/* -- Bottom navigation: quick navigation in bottom bar */
import React, { Component } from 'react';
import { BottomNavigation, Text } from 'react-native-paper';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { ImageBackground, StyleSheet, View, Image, Button } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import {AntDesign} from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';
import { FontAwesome5 } from '@expo/vector-icons';
import { FontAwesome } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';
import { SearchBar } from 'react-native-elements';



const image = { uri: "http://drugshoppa.ng/wp-content/uploads/2020/08/slider3_50.png" };

const StateRoute = () => <View>
<ImageBackground source={image} style={styles.stretch}>
<Image
            source={{
              uri: 'http://drugshoppa.ng/wp-content/uploads/2020/08/cropped-DrugShoppa-1.png',

            }}
            style={styles.logo}
          />

    <Text style={styles.location}><Entypo name="location" size={24} color="red" /> Your store location: Lekki</Text>

    <View style={styles.body}>
      <View style={styles.container}>
        <View style={styles.avata}>
          <FontAwesome name="user-circle-o" size={60} color="#1E90FF" /> Welcome
            <Entypo name="notification" size={24} color="#1E90FF" style={styles.notification}/>
        </View>
            <View style={styles.chart}>
              <Entypo name="chat" size={60} color="white" style={styles.chat}/>
              <Text style={styles.chatText}>Chat With Our Pharmacy</Text>
              <Feather name="phone-call" size={60} color="white" style={styles.phone}/>
        </View>
            <SearchBar
              round
              searchIcon={{ size: 24 }}
              placeholder="Search for Medication..."
              inputContainerStyle={{ backgroundColor: "#E6E6FA" }}
              containerStyle={{
          backgroundColor: "#E6E6FA",
          borderWidth: 0,
          borderRadius: 0,
          borderBottomColor: 'transparent',
          borderTopColor: 'transparent',
          borderColor: "#E6E6FA",
        }}
            />
            <Text style={styles.text}>Product Comming Soon</Text>
      </View>
    </View>
  </ImageBackground>

</View>;

const ClientRoute = () => <Text> Profile</Text>;

const SettingRoute = () => <Text>shop</Text>;

const MembershipRoute = () => <Text>Membership</Text>

const TransactionRoute = () => <Text>Transaction</Text>


export default class BottomNavigatonComponent extends React.Component {
  state = {
    index: 0,
    routes: [
      { key: 'state', title: 'Home', icon: 'home' },
      { key: 'client', title: 'Profile', icon: 'user-alt' },
      { key: 'setting', title: 'Shop', icon: 'shopping-bag' },
      { key: 'Membership', title: 'Membership', icon: 'user' },
      { key: 'Transaction', title: 'Transaction', icon: 'wallet' },
    ],

  };


  _handleIndexChange = index => this.setState({ index });

  _renderScene = BottomNavigation.SceneMap({
    state: StateRoute,
    client: ClientRoute,
    setting: SettingRoute,
    Membership: MembershipRoute,
    Transaction: TransactionRoute,

  });

  render() {
    return (
      <BottomNavigation
        navigationState={this.state}
        onIndexChange={this._handleIndexChange}
        renderScene={this._renderScene}
      />
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  stretch:{
    resizeMode: 'cover',
  },
  logo:{
    width: 100,
    height: 20,
    margin: 30,
  },
  location:{
    margin: 30,
    color: 'red'
  },
  avata:{
    width:80,
    height:80,
    marginLeft:40,
    flex: 1,
    flexDirection: "row",
  },
  chart:{
    backgroundColor: '#1E90FF',
    borderRadius: 15,
    flexDirection: "row",
    padding:40,
    margin:19,
  },
  notification:{
    marginLeft: 180,
  },
  phone:{
    marginLeft: 60,
  },
  chatText:{
    color: 'white',
    fontSize: 18,
    marginLeft: 27,
  },
  body:{
    flex:1,
    backgroundColor:'#E6E6FA',
    borderTopLeftRadius:30,
    borderTopRightRadius:30,
    paddingVertical:50,

  },
  text:{
    flex:1,
    flexDirection: "coloum",
    paddingHorizontal:40,
    fontSize:18,
  }
});
