import React, { Component } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, TextInput, Linking, Platform, StyleSheet } from "react-native";
import firebase from '../database/firebase';
import { Button } from 'react-native-paper';
import Login from './component/Login';



export default class Home extends React.Component {
  makeCall = () => {

    let phoneNumber = '';

    if (Platform.OS === 'android') {
      phoneNumber = 'tel:${+2348093298137}';
    } else {
      phoneNumber = 'telprompt:${+2348093298137}';
    }

    Linking.openURL(phoneNumber);
  };

  constructor() {
    super();
    this.state = { 
      uid: ''
    }
  }

  signOut = () => {
    firebase.auth().signOut().then(() => {
      this.props.navigation.navigate('Login')
    })
    .catch(error => this.setState({ errorMessage: error.message }))
  }  


  render() {
    this.state = { 
      displayName: firebase.auth().currentUser.displayName,
      uid: firebase.auth().currentUser.uid
    }
    return (
      <View>
      <ImageBackground
        source={require("./images/bg.jpg")}
        style={{ width: "100%", height: "100%",resizeMode:"cover"}}
      >

        <View
          style={{
            flexDirection: "row",
            marginTop: 40,
            alignItems: "center",
            paddingHorizontal: 20,
          }}
        >
          <Icon name="bell" size={30} color="#a2a2db" style={{ width: 30 }} />
          <TouchableOpacity
          onPress={() => this.signOut()}
          >
              <Icon name="logout" size={30} color="#a2a2db" style={{ width: 30 }} />
          </TouchableOpacity>
        </View>

        <View style={{ paddingHorizontal: 10, marginTop: 25 }}>
          <Text
            style={{
              fontSize: 15,
              color: "#522289",
              fontFamily: "Roboto",
            }}
          >
            Hello, {this.state.displayName}
          </Text>

          <Text
            style={{
              fontSize: 15,
              paddingVertical: 3,
              paddingRight: 80,
              lineHeight: 22,
              fontFamily: "Roboto",
              color: "#6574CF",
            }}
          >
            PHARMACEUTICAL SERVICES
          </Text>



          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginRight: -40, marginTop: 5 }}
          >
            <View style={{backgroundColor:"#5facdb", flexDirection:"row", padding:15, borderRadius:20}}>
            <TouchableOpacity
              onPress={ ()=>{ Linking.openURL('https://chat.whatsapp.com/EO9wLKN4lWm2kPf9OpJiFh')}}
              style={{
                alignItems: "center",
                justifyContent: "center",
                height: 66,
                width: 66,
                borderRadius: 50,
                backgroundColor: "#ffffff",

              }}
            >
              <Image
                source={require("./images/whatsapp.png")}
                style={{ height: 24, width: 24 }}
              />
            </TouchableOpacity>

            <View
              style={{
                alignItems: "center",
                justifyContent: "center",              

              }}
            >
              <Text style={{paddingHorizontal:7}}>Chat with Our Pharmacits</Text>
            </View>

            <View
              style={{
                alignItems: "center",
                justifyContent: "center",

              }}
            >

            </View>

            <TouchableOpacity
              onPress={this.makeCall}
              style={{
                alignItems: "center",
                justifyContent: "center",
                height: 66,
                width: 66,
                borderRadius: 50,
                backgroundColor: "#ffffff",
              }}
            >
              <Image
                source={require("./images/phone.png")}
                style={{ height: 24, width: 24 }}
              />
            </TouchableOpacity>
            </View>
          </ScrollView>

          <View
            style={{
              flexDirection: "row",
              backgroundColor: "#FFF",
              borderRadius: 40,
              alignItems: "center",
              paddingVertical: 3,
              paddingHorizontal: 20,
              marginTop: 10,
            }}
          >
            <Image
              source={require("./images/search.png")}
              style={{ height: 14, width: 14 }}
            />
            <TextInput
              placeholder="Search for Your Medication"
              style={{ paddingHorizontal: 20, paddingVertical:10, fontSize: 15, color: "#ccccef" }}
            />
          </View>

          <Text
            style={{
              color: "#6574CF",
              fontFamily: "Roboto",
              marginTop: 20,
              fontSize: 17,
              fontWeight:"bold",
            }}
          >
            Top Category
          </Text>

          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginHorizontal: 7, marginTop: 10 }}
          >
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
          </ScrollView>
          <View style={styles.refill}>
            <Text style={{justifyContent:"center", alignContent:"center", paddingVertical:7,color:"#6574CF", fontSize: 19}} >
              Refill Your Prescription

            </Text>
            <Button mode="contained" style={{justifyContent:"center", alignContent:"center",borderRadius:20, paddingVertical:8}} onPress={() => this.props.navigation.navigate('Signup')}>
              Refill Now
            </Button>


          </View>
        </View>
      </ImageBackground>
      </View>
    );
  }
}

const styles = StyleSheet.create({
refill:{
  paddingVertical:50,
  alignItems: 'center',
  justifyContent: 'center',
},
})
