import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { useEffect, useState } from "react";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, FlatList, TextInput, Linking, Platform, StyleSheet, SafeAreaView, KeyboardAvoidingView } from "react-native";
import { AppLoading } from 'expo';
import * as Font from 'expo-font';
import firebase from '../database/firebase';
import { Button, List } from 'react-native-paper';
import WooCommerce from '../servies/woocommerce';
import axios from 'axios';

let customFonts = {
  'Poppins-Bold': require('./font/Poppins-Bold.ttf'),
};

export default class Shop extends React.Component {
  constructor(props) {
    super(props);

}
static navigationOptions = {
  title: 'Products',
};

state = {
  products: []
}

fetProducts = () => {
  const url = `http://drugshoppa.ng/wp-json/wc/v3/products?oauth_consumer_key=ck_aaef5c823b0fabaf2958613ac0c8f60520f42150&oauth_signature_method=HMAC-SHA1&oauth_timestamp=1604806276&oauth_nonce=AzTsmgmM34o&oauth_version=1.0&oauth_signature=U44TcPLd2vxA2iy5ZsLaCph1XWg=`;
  axios.get(url)
  .then(response => this.setState({ products: response.data }))
  .catch(error => console.log('error',error));
}

componentWillMount() {
  this.fetProducts();
}

renderItem = ({item}) => (
  <TouchableOpacity 
    style={styles.listItem} 
    onPress={() => this.props.navigation.navigate("SingleProduct", { product: item })}
  >
    <View style={styles.view}>
      <Image style={styles.image} source={{ uri: item.images[0].src }} />
      <Text style={styles.text}>{item.name}</Text>
    </View>
  </TouchableOpacity>
)

  state = {
    fontsLoaded: false,
  };

  async _loadFontsAsync() {
    await Font.loadAsync(customFonts);
    this.setState({ fontsLoaded: true });
  }

  componentDidMount() {
    this._loadFontsAsync();
  }


  render() {

    if (this.state.fontsLoaded) {
      this.state = { 
        displayName: firebase.auth().currentUser.displayName,
        uid: firebase.auth().currentUser.uid
      }

      return (

        <SafeAreaView style={styles.safeContainer}>
        <StatusBar barStyle="light-content"/>
          <KeyboardAvoidingView behabior='padding' style={styles.container}>
            <ScrollView vertical
                showsVerticalScrollIndicator={false}>
            <View style={{backgroundColor:"#FFF"}}>
              <ImageBackground
              source={require("./images/shop.png")}
              style={{ width: "100%", height: 130, resizeMode:"cover" }}
            >
            <Text style={{  marginTop:58, marginHorizontal:40, fontSize:28, color:'#000080', fontWeight:'bold' }}>Shop</Text>
            <View
              style={{
                flexDirection: "row",
                marginTop: 75,
                alignItems: "center",
                paddingHorizontal: 20,
                justifyContent:"flex-end"
              }}
            >
              <Icon name="bell" size={30} color="#D4AA00" style={{ width: 50,paddingVertical:9, paddingHorizontal:9, borderRadius:25,  backgroundColor:'gray' }} />

            </View>

              </ImageBackground>

            <View style={{ marginTop:10}}>
              <View style={{paddingHorizontal: 10}}>
                  <Text 
                    style={{
                      fontSize: 12,
                      paddingHorizontal:6,
                      paddingRight: 80,
                      margin:6,
                      lineHeight: 22,
                      fontFamily: "Roboto",
                      color: "#000080",
                      fontWeight:'bold',
                      borderRadius:20,


                    }}
                  >
                    <Icon name="map-marker" size={12} color="red" style={{ width: 10 }} />Store: Elfimo Canal West Osapa, Lekki
                  </Text>
                  <View style={styles.hedContainer}>

                          <Image
                                source={require("./images/boy.png")}
                                style={{ height: 40, marginHorizontal:10, width: 40 }}
                          />
                          <Text
                            style={{
                              fontSize: 15,
                              fontFamily: "Poppins-Bold",
                              flexDirection:"row",
                              alignContent:"center",
                              justifyContent:"center",
                              marginTop:8,
                              fontWeight:'bold',
                              color:'#808080'
                            }}
                          >
                            Hello, {this.state.displayName}
                          </Text>
                  </View>
              </View>
                <View style={styles.upContainer}>
                  <ScrollView  showsVerticalScrollIndicator={false}>
                    <List.Section>
                      <List.Accordion
                        title="Category"
                        >
                          <ScrollView vertical showsVerticalScrollIndicator={false}>
                        <List.Item title="First item" />
                        <List.Item title="Second item" />
                        <List.Item title="First item" />
                        <List.Item title="Second item" />
                        <List.Item title="First item" />
                        <List.Item title="Second item" />
                        <List.Item title="First item" />
                        <List.Item title="Second item" />
                        <List.Item title="First item" />
                        <List.Item title="Second item" />
                        </ScrollView>
                      </List.Accordion>

                    </List.Section>
                  </ScrollView>
                  <ScrollView style={styles.container}>
        {
          this.state.products ?
          <FlatList
            contentContainerStyle={styles.list} 
            numColumns={2}
            data={this.state.products}
            keyExtractor={ item => item.id.toString() }
            renderItem={this.renderItem}
          />
          :
          <View style={styles.loaderContainer}>
            <Image
              source={ require('./images/cart-loading.gif') }
              style={styles.loaderImage}
            />
          </View>
        }
      </ScrollView>
                </View>  
              </View>
            </View>
          </ScrollView>
        </KeyboardAvoidingView>
      </SafeAreaView>        

      );
    } else {
      return <AppLoading />;
    }
  }
}

const styles = StyleSheet.create({
  refill:{

    flexDirection:'row',
    backgroundColor:"#003380",
    marginTop:1,
    paddingVertical:5,
    paddingBottom:8,
    marginBottom:8
  },
  safeContainer:{
  marginTop:30
  },
  hedContainer:{
    flexDirection:"row",
    height: 80,
    paddingVertical:9,
    width:180,

  },
  bana:{
    width:350
  },
  chatContainer:{
    backgroundColor:"#FFF", 
    flexDirection:"row", 
    padding:15, 
    borderRadius:1,
    shadowColor: 'black',
    shadowOffset: {width: 0, height: 2},
    shadowOpacity: 0.3,
    elevation: 10,
  },
  upContainer:{
    paddingBottom:20,
    paddingHorizontal:20,
    shadowColor: 'black',
    shadowOffset: {width: 0, height: 2},
    shadowOpacity: 0.3,
    elevation: 10,
    justifyContent:"center",
    alignContent:"center"
  },
  inputStyle: {
    width: '100%',
    marginBottom: 15,
    alignSelf: "center",
    borderColor: "#ccc",
    borderBottomWidth: 1
  },
  list: {
    flexDirection: 'column'
  },
  listItem: {
    width: '50%'
  },
  view: {
    padding: 10
  },
  image: {
    width: 150, 
    height: 150
  },
  text: {
    textAlign: 'center',
    fontSize: 16,
    padding: 5
  },
  loaderContainer: {
    alignItems: 'center', 
    justifyContent: 'center',
  },
  loaderImage: {
    width: 200,
    height: 200,
  },
  })
