import React, { Component } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, TextInput, Linking, Platform, StyleSheet } from "react-native";
import firebase from '../database/firebase';
import DrawerScreen from './DrawerScreen';
import { Button } from 'react-native-paper';



export default class Home extends React.Component {
  makeCall = () => {

    let phoneNumber = '';

    if (Platform.OS === 'android') {
      phoneNumber = 'tel:${08144707400}';
    } else {
      phoneNumber = 'telprompt:${08144707400}';
    }

    Linking.openURL(phoneNumber);
  };

  constructor() {
    super();
    this.state = { 
      uid: ''
    }
  }

  signOut = () => {
    firebase.auth().signOut().then(() => {
      this.props.navigation.navigate('Login')
    })
    .catch(error => this.setState({ errorMessage: error.message }))
  }  




  render() {
    this.state = { 
      displayName: firebase.auth().currentUser.displayName,
      uid: firebase.auth().currentUser.uid
    }
    return (
      <ImageBackground
        source={require("./images/bg.jpg")}
        style={{ width: "100%", height: "100%" }}
      >

        <View
          style={{
            flexDirection: "row",
            marginTop: 40,
            alignItems: "center",
            paddingHorizontal: 10,
          }}
        >
          <Icon name="bell" size={30} color="#a2a2db" style={{ width: 20 }} />
          <TouchableOpacity
          onPress={() => this.signOut()}

            >
          </TouchableOpacity>
        </View>

        <View style={{ paddingHorizontal: 40, marginTop: 25 }}>
          <Text
            style={{
              fontSize: "20",
              color: "#522289",
              fontFamily: "RobotoBold",
            }}
          >
            Shop,  {this.state.displayName}
          </Text>



          <View
            style={{
              flexDirection: "row",
              backgroundColor: "#FFF",
              borderRadius: 40,
              alignItems: "center",
              paddingVertical: 10,
              paddingHorizontal: 20,
              marginTop: 30,
            }}
          >
            <Image
              source={require("./images/search.png")}
              style={{ height: 14, width: 14 }}
            />
            <TextInput
              placeholder="Search for Your Medication"
              style={{ paddingHorizontal: 20, paddingVertical:10, fontSize: "15", color: "#ccccef" }}
            />
          </View>

          <Text
            style={{
              color: "#6574CF",
              fontFamily: "RobotoRegular",
              marginTop: 50,
              fontSize: "17",
              fontWeight:"bold",
            }}
          >
            Top Category
          </Text>

          <ScrollView
            horizontal
            showsHorizontalScrollIndicator={false}
            style={{ marginHorizontal: 7, marginTop: 10, flexDirection:"row" }}
          >
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
            <View
              style={{
                backgroundColor: "#FEFEFE",
                height: 100,
                width: 100,
                borderRadius: 15,
                padding: 5,
                margin:2,
              }}
            >
              <Image
                source={require("./images/1.jpg")}
                style={{ width: 90, borderRadius: 10, height: 90 }}
              />
            </View>
          </ScrollView>
          <Text style={{justifyContent:"center", alignContent:"center", paddingVertical:7,color:"#6574CF", fontSize:"19"}} >
              Product Coming Soon

            </Text>

          <View style={styles.refill}>
            <Text style={{justifyContent:"center", alignContent:"center", paddingVertical:7,color:"#6574CF", fontSize:"19"}} >
              Refill Your Prescription

            </Text>
            <Button mode="contained" style={{justifyContent:"center", alignContent:"center",borderRadius:20, paddingVertical:8}} onPress={() => this.props.navigation.navigate('Signup')}>
              Refill Now
            </Button>
          </View>
        </View>
      </ImageBackground>
    );
  }
}

const styles = StyleSheet.create({
refill:{
  paddingVertical:50,
  alignItems: 'center',
  justifyContent: 'center',
},
})
