import React, { Component } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, Linking, Platform, StyleSheet } from "react-native";
import firebase from '../database/firebase';
import { Button, TextInput } from 'react-native-paper';



export default class Home extends React.Component {
  makeCall = () => {

    let phoneNumber = '';

    if (Platform.OS === 'android') {
      phoneNumber = 'tel:${08144707400}';
    } else {
      phoneNumber = 'telprompt:${08144707400}';
    }

    Linking.openURL(phoneNumber);
  };

  constructor() {
    super();
    this.state = { 
      uid: ''
    }
  }

  signOut = () => {
    firebase.auth().signOut().then(() => {
      this.props.navigation.navigate('Login')
    })
    .catch(error => this.setState({ errorMessage: error.message }))
  }  




  render() {
    this.state = { 
      displayName: firebase.auth().currentUser.displayName,
      uid: firebase.auth().currentUser.uid
    }
    return (
      <ImageBackground
        source={require("./images/bg.jpg")}
        style={{ width: "100%", height: "100%" }}
      >

        <View
          style={{
            flexDirection: "row",
            marginTop: 40,
            alignItems: "center",
            paddingHorizontal: 10,
          }}
        >
          <Icon name="bell" size={30} color="#a2a2db" style={{ width: 20 }} />
          <TouchableOpacity
          onPress={() => this.signOut()}

            >
          </TouchableOpacity>
        </View>

        <View style={{ paddingHorizontal: 40, marginTop: 25 }}>
          <Text
            style={{
              fontSize: "20",
              color: "#522289",
              fontFamily: "RobotoBold",
            }}
          >
            Hello, {this.state.displayName}
          </Text>


        <Text style={{justifyContent:"center", alignContent:"center", paddingVertical:20,color:"#6574CF", fontSize:"19"}} >
              Please Fill the Correct details
              Seperate the Drugs Names and Numbers with a coma (,)
            </Text>
        <TextInput
          style={styles.inputStyle}
          placeholder="Drugs Names"

        />
        <TextInput
          style={styles.inputStyle}
          placeholder="Drugs ID Numbers"

        />
        <TextInput
          style={styles.inputStyle}
          placeholder="Quantity"
        />
        <TextInput
          style={styles.inputStyle}
          placeholder="Medications"
        />   

            <Button mode="contained" style={{justifyContent:"center", alignContent:"center",borderRadius:20, paddingVertical:8, marginTop:12}}>
              Request
            </Button>

        </View>
      </ImageBackground>
    );
  }
}

const styles = StyleSheet.create({
refill:{
  paddingVertical:50,
  alignItems: 'center',
  justifyContent: 'center',
},
})
