import React, { useState } from 'react';
import Icon from "@expo/vector-icons/MaterialCommunityIcons";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { View, Text, Image, ImageBackground, Linking, Platform, StyleSheet } from "react-native";
import firebase from '../database/firebase';
import DrawerScreen from './DrawerScreen';
import { Switch } from 'react-native-paper';


const App = () => {
    const [isEnabled, setIsEnabled] = useState(false);
    const toggleSwitch = () => setIsEnabled(previousState => !previousState);

    return (
      <View style={styles.container}>
          <View style={styles.setContainer}>
            <Text style={{paddingRight:160}}>Notification</Text>
            <Switch
            trackColor={{ false: "#767577", true: "#81b0ff" }}
            thumbColor={isEnabled ? "#f5dd4b" : "#f4f3f4"}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={isEnabled}
            />
          </View>
          <View style={styles.setContainer}>
            <Text style={{paddingRight:100}}>Notification Sound</Text>
            <Switch
            trackColor={{ false: "#767577", true: "#81b0ff" }}
            thumbColor={isEnabled ? "#f5dd4b" : "#f4f3f4"}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={isEnabled}
            />
          </View>
          <View style={styles.setContainer}>
            <Text style={{paddingRight:150}}>Security</Text>
            <Switch
            trackColor={{ false: "#767577", true: "#81b0ff" }}
            thumbColor={isEnabled ? "#f5dd4b" : "#f4f3f4"}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={isEnabled}
            />
          </View>
      </View>

    );
  }
const styles = StyleSheet.create({
container:{
    flex:1,
  padding:20,
  marginTop:40,
  flexDirection:"column"
},
setContainer:{
  flexDirection:"row",
  paddingVertical:20,
}
})

export default App;
